{
  match e
  case Const(v) => Const(v)
  case Var(x) => Var(x)
  case Add(Const(0), e2) => optimize(e2)
  case Add(e1, Const(0)) => optimize(e1)
  case Add(e1, e2) => 
    var o1 := optimize(e1);
    var o2 := optimize(e2);
    match o1
    case Const(0) => o2
    case _ => match o2
      case Const(0) => o1
      case _ => Add(o1, o2)
}

