  ensures optimal(optimize(e))
{
  match e
  case Const(_) =>
  case Var(_) =>
  case Add(e1, e2) =>
    /*[CODE HERE]*/;
    OptimizerOptimal(e2);
    var o1 := optimize(e1);
    var o2 := optimize(e2);
    assert optimal(o1) && optimal(o2);
}

