// Graph operations: path, reachable, cycle detection

type Node = nat

datatype Graph = Graph(nodes: set<Node>, edges: set<(Node, Node)>)

predicate {:spec} validGraph(g: Graph)
{
  forall e :: e in g.edges ==> e.0 in g.nodes && e.1 in g.nodes
}

predicate {:spec} hasEdge(g: Graph, from: Node, to: Node)
  requires validGraph(g)
{
  (from, to) in g.edges
}

function {:spec} neighbors(g: Graph, n: Node): set<Node>
  requires validGraph(g)
  requires n in g.nodes
{
  set m | m in g.nodes && (n, m) in g.edges
}

predicate {:spec} isPath(g: Graph, path: seq<Node>)
  requires validGraph(g)
{
  |path| > 0 &&
  (forall i :: 0 <= i < |path| ==> path[i] in g.nodes) &&
  (forall i :: 0 <= i < |path| - 1 ==> hasEdge(g, path[i], path[i+1]))
}

ghost predicate {:spec} reachable(g: Graph, from: Node, to: Node)
  requires validGraph(g)
  requires from in g.nodes && to in g.nodes
{
  exists path :: isPath(g, path) && |path| >= 1 &&
    path[0] == from && path[|path| - 1] == to
}

ghost predicate {:spec} hasCycle(g: Graph)
  requires validGraph(g)
{
  exists path :: isPath(g, path) && |path| > 1 && path[0] == path[|path| - 1]
}

ghost predicate {:spec} isAcyclic(g: Graph)
  requires validGraph(g)
{
  !hasCycle(g)
}

ghost predicate {:spec} isTree(g: Graph)
  requires validGraph(g)
{
  isAcyclic(g) &&
  |g.edges| == |g.nodes| - 1 &&
  forall n, m :: n in g.nodes && m in g.nodes && n != m ==> reachable(g, n, m)
}

function {:spec} inDegree(g: Graph, n: Node): nat
  requires validGraph(g)
  requires n in g.nodes
{
  |set m | m in g.nodes && (m, n) in g.edges|
}

function {:spec} outDegree(g: Graph, n: Node): nat
  requires validGraph(g)
  requires n in g.nodes
{
  |neighbors(g, n)|
}

ghost predicate {:spec} isConnected(g: Graph)
  requires validGraph(g)
{
  forall n, m :: n in g.nodes && m in g.nodes ==> reachable(g, n, m)
}

// Simplified version without recursion
ghost function {:spec} graphDistance(g: Graph, from: Node, to: Node): nat
  requires validGraph(g)
  requires from in g.nodes && to in g.nodes
  requires reachable(g, from, to)
{
  var path :| isPath(g, path) && |path| >= 1 &&
    path[0] == from && path[|path| - 1] == to;
  |path| - 1
}

lemma {:axiom} SelfReachable(g: Graph, n: Node) requires validGraph(g) requires n in g.nodes ensures reachable(g, n, n);

lemma {:axiom} DirectEdgeReachable(g: Graph, a: Node, b: Node) requires validGraph(g) requires a in g.nodes && b in g.nodes requires hasEdge(g, a, b) ensures reachable(g, a, b);

lemma PathOfLengthTwo(g: Graph, path: seq<Node>)
  requires validGraph(g)
  requires isPath(g, path)
  requires |path| == 2
  ensures path[0] in g.nodes && path[1] in g.nodes
  ensures hasEdge(g, path[0], path[1])
{
  assert 0 <= 0 < |path|;
  assert path[0] in g.nodes;
  assert 0 <= 1 < |path|;
  assert path[1] in g.nodes;
  assert 0 <= 0 < |path| - 1;
  /*[CODE HERE]*/;
}

lemma {:axiom} TreeNoCycle(g: Graph) requires validGraph(g) requires isTree(g) ensures isAcyclic(g);

lemma {:axiom} EmptyGraphAcyclic(g: Graph) requires validGraph(g) requires g.edges ==;
  ensures isAcyclic(g)
{
  if hasCycle(g) {
    var path :| isPath(g, path) && |path| > 1 && path[0] == path[|path| - 1];
    assert |path| >= 2;
    assert hasEdge(g, path[0], path[1]);
    assert (path[0], path[1]) in g.edges;
    assert false;
  }
}

lemma {:axiom} SingleNodeAcyclic(g: Graph) requires validGraph(g) requires |g.nodes| == 1 requires g.edges ==;
  ensures isAcyclic(g)
{
  EmptyGraphAcyclic(g);
}

lemma {:axiom} ValidGraphSubgraph(g: Graph, removed: Node) requires validGraph(g) requires removed in g.nodes ensures validGraph(Graph(g.nodes -;
    set e | e in g.edges && e.0 != removed && e.1 != removed))
{
  var g' := Graph(g.nodes - {removed},
    set e | e in g.edges && e.0 != removed && e.1 != removed);
  forall e | e in g'.edges
    ensures e.0 in g'.nodes && e.1 in g'.nodes
  {
    assert e in g.edges;
    assert e.0 != removed && e.1 != removed;
    assert e.0 in g.nodes && e.1 in g.nodes;
    assert e.0 in g'.nodes && e.1 in g'.nodes;
  }
}

// Min-heap operations: insert, extractMin, heapify with heap property

datatype Heap = Heap(elements: seq<int>)

// Check if heap is empty
predicate {:spec} heapIsEmpty(h: Heap)
{
  |h.elements| == 0
}

// Get heap size
function {:spec} heapSize(h: Heap): nat
{
  |h.elements|
}

// Parent index in heap
function {:spec} parent(i: nat): nat
  requires i > 0
{
  (i - 1) / 2
}

// Left child index
function {:spec} leftChild(i: nat): nat
{
  2 * i + 1
}

// Right child index
function {:spec} rightChild(i: nat): nat
{
  2 * i + 2
}

// Check min-heap property
predicate {:spec} isMinHeap(h: Heap)
{
  forall i :: 0 < i < |h.elements| ==> h.elements[parent(i)] <= h.elements[i]
}

// Check max-heap property
predicate {:spec} isMaxHeap(h: Heap)
{
  forall i :: 0 < i < |h.elements| ==> h.elements[parent(i)] >= h.elements[i]
}

// Get minimum element (root of min-heap)
function {:spec} getMin(h: Heap): int
  requires !heapIsEmpty(h)
  requires isMinHeap(h)
{
  h.elements[0]
}

// Get maximum element (root of max-heap)
function {:spec} getMax(h: Heap): int
  requires !heapIsEmpty(h)
  requires isMaxHeap(h)
{
  h.elements[0]
}

// Check if element exists in heap
predicate {:spec} heapContains(h: Heap, x: int)
{
  x in h.elements
}

// Create empty heap
function {:spec} emptyHeap(): Heap
{
  Heap([])
}

// Create heap from single element
function {:spec} singletonHeap(x: int): Heap
{
  Heap([x])
}

// Swap two elements in sequence
function {:spec} swap(s: seq<int>, i: nat, j: nat): seq<int>
  requires i < |s| && j < |s|
{
  s[i := s[j]][j := s[i]]
}

// Check if heap is complete binary tree (always true for array representation)
predicate {:spec} isComplete(h: Heap)
{
  true  // Array representation is always complete
}

// Get height of heap
function {:spec} heapHeight(h: Heap): nat
{
  if |h.elements| == 0 then 0
  else log2Floor(|h.elements|) + 1
}

// Floor of log base 2
function {:spec} log2Floor(n: nat): nat
  requires n > 0
  decreases n
{
  if n == 1 then 0
  else 1 + log2Floor(n / 2)
}

// Check if index is valid
predicate {:spec} validIndex(h: Heap, i: nat)
{
  i < |h.elements|
}

// Get all elements at a given level
function {:spec} getLevel(h: Heap, level: nat): seq<int>
{
  getLevelHelper(h, level, 0)
}

function getLevelHelper(h: Heap, targetLevel: nat, currentLevel: nat): seq<int>
  decreases |h.elements| - currentLevel
{
  if currentLevel >= |h.elements| || currentLevel >= Power2(targetLevel) then []
  else if currentLevel >= Power2(targetLevel) - 1 && currentLevel < Power2(targetLevel + 1) - 1 then
    if currentLevel < |h.elements| then [h.elements[currentLevel]] + getLevelHelper(h, targetLevel, currentLevel + 1)
    else []
  else getLevelHelper(h, targetLevel, currentLevel + 1)
}

function Power2(n: nat): nat
{
  if n == 0 then 1
  else 2 * Power2(n - 1)
}

