lemma SumToFormula(n: nat)
  ensures sumTo(n) == n * (n + 1) / 2
{
  if n == 0 {
    // Base case
    /*[CODE HERE]*/;
    assert 0 * (0 + 1) / 2 == 0;
  } else {
    // Inductive step
    SumToFormula(n - 1);

    calc == {
      sumTo(n);
      n + sumTo(n - 1);
