function {:spec} modPow(base: nat, exp: nat, m: nat): nat
  requires m > 0
  decreases exp
{
  if exp == 0 then 1 % m
  else if exp % 2 == 0 then
    var half := modPow(base, exp / 2, m);
    (half * half) % m
  else
    ((base % m) * modPow(base, exp - 1, m)) % m
}

