function power(base: int, exp: nat): int
{
  if exp == 0 then 1
  else base * power(base, exp - 1)
}

function {:spec} powerSpec(base: int, exp: nat): int
  decreases exp
{
  if exp == 0 then 1
  else base * powerSpec(base, exp - 1)
}

lemma {:axiom} powerCorrect(base: int, exp: nat) ensures power(base, exp) == powerSpec(base, exp);

lemma {:axiom} powerZero(base: int) ensures power(base, 0) == 1;

lemma {:axiom} powerOne(base: int) ensures power(base, 1) == base;

lemma powerAdd(base: int, exp1: nat, exp2: nat)
  ensures power(base, exp1 + exp2) == power(base, exp1) * power(base, exp2)
{
  if exp1 == 0 {
    calc {
      power(base, exp1 + exp2);
      == power(base, 0 + exp2);
      == power(base, exp2);
      == 1 * power(base, exp2);
      == power(base, 0) * power(base, exp2);
      == power(base, exp1) * power(base, exp2);
    }
  } else {
    /*[CODE HERE]*/;
    calc {
      power(base, exp1 + exp2);
      == { assert exp1 + exp2 == (exp1 - 1) + exp2 + 1; }
      power(base, (exp1 - 1) + exp2 + 1);
      == base * power(base, (exp1 - 1) + exp2);
      == { powerAdd(base, exp1 - 1, exp2); }
      base * (power(base, exp1 - 1) * power(base, exp2));
      == (base * power(base, exp1 - 1)) * power(base, exp2);
      == power(base, exp1) * power(base, exp2);
    }
  }
}

// Queue operations: enqueue, dequeue, isEmpty, size

datatype Queue<T> = Queue(front: seq<T>, rear: seq<T>)

function {:spec} queueEmpty<T>(): Queue<T>
{
  Queue([], [])
}

predicate {:spec} queueIsEmpty<T>(q: Queue<T>)
{
  q.front == [] && q.rear == []
}

function {:spec} enqueue<T>(q: Queue<T>, x: T): Queue<T>
{
  Queue(q.front, [x] + q.rear)
}

function {:spec} dequeue<T>(q: Queue<T>): Queue<T>
  requires !queueIsEmpty(q)
{
  if |q.front| > 0 then
    Queue(q.front[1..], q.rear)
  else
    // Front is empty, so we reverse rear and put it in front
    if |q.rear| == 1 then
      Queue([], [])
    else
      Queue(reverse(q.rear)[1..], [])
}

function {:spec} queueFront<T>(q: Queue<T>): T
  requires !queueIsEmpty(q)
{
  if |q.front| > 0 then
    q.front[0]
  else
    // Front is empty, so the front element is the first element of rear (when reversed)
    q.rear[|q.rear| - 1]
}

function {:spec} queueSize<T>(q: Queue<T>): nat
{
  |q.front| + |q.rear|
}

function {:spec} queueToSeq<T>(q: Queue<T>): seq<T>
{
  q.front + reverse(q.rear)
}

function reverse<T>(s: seq<T>): seq<T>
{
  if |s| == 0 then []
  else reverse(s[1..]) + [s[0]]
}

