datatype List = Nil | Cons(head: int, tail: List)

predicate SortedBetween(l: List, lo: int, hi: int)
{
  match l
    case Nil => true
    case Cons(x, xs) => lo <= x < hi && SortedBetween(xs, x, hi)
}

