    var p := 0;
    var q := |s|;
    if p == q
    {
        return p;
    }
    while p != q 
        decreases q-p;
        invariant 0 <= p <= q <= |s|;
        invariant forall r | 0 <= r < p :: s[r] <= x;
        invariant forall r | q <= r < |s| :: s[r] >= x;
