   ensures product == SetProduct((set i | 0 <= i < arr.Length :: arr[i]))
{
    var p := 1;
    var seen: set<int> := {};
    for i := 0 to arr.Length
        invariant 0 <= i <= arr.Length
        invariant seen == (set k | 0 <= k < i :: arr[k])
        invariant p == SetProduct((set k | 0 <= k < i :: arr[k]))
    {
        if ! (arr[i] in seen) {
