    for i := 0 to |s|
invariant NoLetters(s, 0) <==> i == 0
invariant !NoLetters(s, i) ==> forall k :: 0 <= k < |s| - i && !IsLetter(s[k]) ==> result[k]==s[k]
invariant !NoLetters(s, i) ==> forall k :: 0 <= k < i && IsLetter(s[k]) ==> result[k] == ToggleCase(s[k])
invariant 0 <= i <= |s|
    {
